css:
body {
    font-weight: 12px;
    margin: 0 1px;
}

.sub-header {
    font-weight: bold;
    font-size: 12px;
}

.header {
    display: flex;
    justify-content: space-between;
    /*border: 1px solid black;*/
    /*padding: 2px 10px;*/
}

.company-header {
    font-weight: bold;
    font-size: 20px;
}

.dc-container {
    display: flex;
    justify-content: center;
    height: auto;
    align-items: center;
}

.address-and-details {
    display: flex;
    justify-content: space-between;
    /*border: 1px solid black;*/
}

.dc-data {
    display: flex;
    flex-direction: column;
}

.dc-data-table, .product-table {
    border-collapse: collapse;
    width: 100%;
}

.dc-data-table td, .dc-data-table th, .product-table td, .product-table th {
    /*border: 1px solid black;*/
    padding: 2px !important;
    margin: 0 !important;
    /*font-size: 10px;*/
    /*text-align: center;*/
}

.product-table td, .product-table th {
    border: 1px solid black;
}

.dc-data-table th, .product-table th {
    font-size: 12px;
    color: black;
    font-weight: bold;
    padding-right: 30px!important;
}

.product-table th {
    background-color: #f2f2f2 !important;
}

.table-container {
    padding-top: 10px;
}

.delivery-address {
    display: flex;
    /*gap: 20px;*/
    padding-top: 10px;
}

.delivery-address > div {
    flex: 1;
    /*border: 1px solid black;*/
}

.footer {
    padding-top: 10px;
}
