css:
        body {
            font-family: Arial, sans-serif;
            margin: 20px;
        }
        
        .print-format {
          font-family: "Times New Roman", Times, serif;
        }


        .job-card {
            /*border: 1px solid #000;*/
            width: 100%;
        }
        .job-card-1 {
            /*border: 1px solid #000;*/
            padding-top: 10%;
            width: 100%;
        }

        .header {
            font-size: 20px;
            font-weight: bold;
            text-align: center;
        }

        .header h2 {
            margin: 0;
        }

        .details {
            width: 100%;
            border-collapse: collapse;
        }
        
        .details-h {
            width: 100%;
            border-collapse: collapse;
            justify-content: center;
        }

        .details th,
        .details td,
        .details-h th,
        .details-h td {
            border: 1px solid #000;
            padding: 5px !important;
        }
        
        .details th,
        .details-h th{
            color: black;
            font-weight: bold;
        }

        .extra-note {
            margin-top: 20px;
        }

        .signature {
            margin-top: 40px;
        }
        
        .no-border-bottom {
            border-bottom: none !important;
        }
