@page {
    size: landscape;
}
/*<!--<meta name="pdfkit-orientation" content="Landscape"/>-->*/

body {
    margin: 0 1px 0 0 ;
    padding:0;
    line-height: normal;
    font-weight: bold;
}

div, p {
    font-size: 11px;
    color: #252525;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: auto;
}

.header > div {
    /*flex: 1;*/
    /*border: 1px solid black;*/
}

.logo-container {
    width: 20%;
    display: flex;
    justify-content: flex-start;
    /*background-color: red;*/
}

.irn-ack-details {
    width: 50%;
    display: flex;
    flex-direction: column;
    /*background-color: blue;*/
}

.tax-inv {
    font-size: 25px;
    font-weight: bold;
    text-align: center;
}

.qr-code {
    width: 20%;
    display: flex;
    justify-content: flex-end;
    height: auto;
    align-items: center;
    /*background-color: green;*/
}

.title-container{
    display: flex;
    justify-content: center;
    height: auto;
    align-items: center;
}

.title {
    font-weight: bold;
    font-size: 20px;
}

.ack-num-date {
    display: flex;
    gap: 25px
}

.supply-source {
    transform: translateY(-18%);   
}

.sticky-data {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    padding-top: 5px;
}


.bill-ship-to {
    display: flex;
    justify-content: space-between;
    width: 100%;
}

.supply-source > div, .bill-ship-to > div{
    flex: 1;
}

.sub-header {
    font-weight: bold;
}

.center {
    text-align: center;
}

.tax-table-container {
    display: flex;
    justify-content: center;
    padding-top: 10px;
}

.tax-table-container > div {
    flex: 1;
    /*gap : 25px;*/
}

.make-center {
    text-align: center;
}

.tax-table-in-words {
    display: flex;
    background-color: gray;
}

.custom-tax-breakup-table {
    /*background-color: green;*/
}

.custom-tax-breakup-table table {
    width: 100% !important;
    border-collapse: collapse !important;
    margin: 0 !important;
    padding: 0 !important;
}

.custom-tax-breakup-table th, 
.custom-tax-breakup-table td {
    padding: 3px !important;
    margin: 15px !important;
    border: 1px solid #000 !important; 
    text-align: left !important;
}

.custom-tax-breakup-table th {
    background-color: #f2f2f2 !important;
    color: black !important;
    font-weight: bold !important;
    font-size: 13px !important;
}

.si-terms {
    padding: 2px 5px;
}

.in-words {
    border: 1px solid black;
    border-left: none;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 0 5px 0 5px;
    /*gap: 5px;*/
    height: auto;
}

.in-words > h3{
    font-size: 15px;
    padding: 0 10px;
}


.before-table {
    padding-top: 5px;
}

.flex-div {
    display: flex;
    /*padding-top: 10px;*/
    /*border-bottom: 1px solid black;*/
}

.flex-div > .terms {
    /*flex: 2;*/
    border-right: 1px solid black;
    width: 48%;
}

.terms-conditions {
    padding-left: 10px;
    font-weight: bold;
}

.tc-details {
    padding-left: 10px;
}

.td-details {
    padding-left: 10px;
    background-color: red;
}

.sign-space, .auth-rep {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    /*flex: 1;*/
    width: 26%;
}

.sign-space {
    border-right: 1px solid black;
}

.sign-space > div > p , .auth-rep > div > p {
    display: flex;
    justify-content: center;
    margin: 6px;
}

.empty-div {
        padding-top: 10px;
}

.footer-part {
    border: 1px solid black;
    border-bottom: none;
    
}

.bank-pan-details, .created-approved-by {
    display: flex;
    justify-content: space-between;
    border-top: none;
}

.created-approved-by {
    border-top: 1px solid black;
}

.com-gen-invoice {
    display: flex;
    justify-content: center;
    /*border-top: 1px solid black;*/
    padding-top: 2px;
}

.bank-details {
    font-weight: bold;
    padding: 2px 5px;
    width: 58.8%;
    border-right: 1px solid black;
}

.company-pan {
    display: flex;
    padding: 2px 5px;
    width: 40%;
}

.company-pan > span{
    flex: 1;
    border: 2px solid red;
}

.items-table {
    padding-top: 10px;
}

.tax-table, .items-table, .footer-table {
    border-collapse: collapse;
    width: 100%;
}

.items-table, .items-table td, .tax-table, .tax-table td, .footer-table td {
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
}


.footer-table td {
    padding: 0px !important;
    margin: 0 !important;
}

.items-table th, .tax-table th, .footer-table th {
    background-color: #f2f2f2 !important;
    background-color: green;
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    color: black;
    font-weight: bold;
    font-size: 13px !important;
    text-align: center;
}

.left-align {
    text-align: left;
}

.digital_sign {
    display: flex;
    justify-content: center;
    /*padding: 0px 20px 0px 10px;*/
}

.center-div {
    text-align: center;
}

.lut-and-sez {
    padding-top: 10px;
}

.serial-no {
    /*background-color: red;*/
    width: auto;
