{%- set sales_order = frappe.get_doc("Sales Order", doc.items[0].sales_order or "") %}
<!--To Get Approver derails---->
{%- set comments = frappe.get_all(
    "Comment",
    filters={
        "reference_doctype": "Sales Order",
        "reference_name": doc.items[0].sales_order or "",
        "content": "Approved"
    },
    fields=["owner", "creation", "content"]
) %}

<!--To get Location based address -->
{% set location = sales_order.custom_location or '' %}

{% if location == "Chennai" %}
  {% set address = frappe.get_doc("Address", "Protocol Labels India Pvt Ltd.,") %}
{% elif location %}
  {% set address = frappe.get_doc("Address", "Protocol Labels India Private Limited-Billing") %}
{% else %}
  {% set address = None %}
{% endif %}

<!--To check currency type:-->
{% set currency_namespace = namespace(currency_type='') %}

{% if doc.currency == 'INR' %}
    {% set currency_namespace.currency_type = '₹' %}
{% elif doc.currency == 'USD' %}
    {% set currency_namespace.currency_type = '$' %}
{% else %}
    {% set currency_namespace.currency_type = doc.currency + ': ' %}
{% endif %}

<!--To fetch the serial numbers against each item:-->
{% macro get_serial_numbers(item_code, rate, delivery_note) %}
  {% set doc = frappe.get_doc('Delivery Note', delivery_note) %}
  {% set serial_numbers = [] %}

  {# Collect serial numbers for the specific item_code and rate #}
  {% for item in doc.items %}
    {% if item.item_code == item_code and item.rate == rate and item.serial_and_batch_bundle %}
      {% set serial_bundle = frappe.get_doc('Serial and Batch Bundle', item.serial_and_batch_bundle) %}
      
      {% for entry in serial_bundle.entries %}
        {% set _ = serial_numbers.append(entry.serial_no) %}
      {% endfor %}
    {% endif %}
  {% endfor %}

  {# Return serial numbers as a comma-separated string #}
  {{ serial_numbers | join(', ') }}
{% endmacro %}

