css:
body {
    font-family: Arial;
}

p {
    font-size: 10px;
    color: #252525;
    letter-spacing: 0.5px;
    line-height: 1.5;
}

.border {
    border: 2px solid black;
    margin: 0;
    /*padding: 0 0 15px 0;*/
}

.header {
    border-bottom: 2px solid black;
}

.header > p{
    padding-left: 15px;
    font-weight: bold;
    font-size: 18px;
    /*margin : 0;*/
}

.comapany-address {
    height: auto;
    align-items: center;
}

.sub-header {
    font-weight: bold;
    font-size: 10px;
}

.total-tax {
    font-weight: bold;
    font-size: 14px;
    /*display: flex;*/
    /*justify-content: center;*/
}

.flex-div {
    display: flex;
    /* gap: 10px; */
}

.flex-div > div {
    flex: 1;
    border-bottom: 1px solid black;
}

.logo-container {
    display: flex;
    justify-content: center;
    height: auto;
    align-items: center;
    width: 32%;
}

.company-details {
    display: flex;
    justify-content: space-around;
    /*gap: 6px;*/
    height: auto;
    align-items: center;
}

.quote-details-div {
    border-left: 2px solid black;
}

.quote-details {
    display: flex;
    justify-content: space-between;
    border-bottom: 2px solid black;
}

.quote-details > p, .last-quote-details > p {
    padding: 1px 5px;
    margin: 0;
}

.last-quote-details {
    display: flex;
    justify-content: space-between;
}


.total {
    width: 25%;
}

.bill-ship-details {
    display: flex;
}

.bill-ship-details > div {
    flex: 1;
}

.bill-ship-address {
    display: flex;
    flex-direction: column;
}

.bill-ship-address > div > p, .bank-details{
    padding: 2px 8px;
    margin: 0;
}

.bill-address {
    border-bottom: 2px solid black;
}

.equal-div {
    display: flex;
}

.equal-div > div {
    flex: 1;
    /*border: 1px solid #000; */
    border-top: 2px solid #000; 
}
.equal-div > div > p{
    margin: 0;
    padding: 2px 8px;
}

.table-container{
    border-collapse: collapse;
    width: 100%;
}

.table-container td {
    border: 2px solid black;
    padding: 3px !important;
    margin: 0 !important;
    font-size: 10px;
    /*text-align: center;*/
}

.table-container th {
    background-color: #f2f2f2 !important;
    background-color: green;
    border: 2px solid black;
    padding: 3px !important;
    margin: 0 !important;
    color: black;
    font-weight: bold;
    text-align: center;
}

td.bordered-cell {
    border: none;
}

.tax-table {
    padding-top: 10px;
}

.cost-details {
    padding-top: 10px;
}

.left-align {
    text-align: left;
}

.bordered-cell {
    border-left: 0px rgb(255, 255, 255);
    border-bottom: 0px rgb(255, 255, 255);
    border-top: 0px rgb(255, 255, 255);
}

.digital_sign {
    display: flex;
    justify-content: center;
    padding: 5px 0px;
}

.comp-inv {
    display: flex;
    justify-content: center;
}

.contact-for-query {
    display: flex;
}

.contact-for-query > div{
    flex: 1;
    display: flex;
    justify-content: center;
    height: auto;
    align-items: center;
}

.footer-part {
    display: flex;
    flex-direction: column;
}

.footer-part > div {
    flex: 1;
    /*border: 2px solid #000; */
    border-bottom: 2px solid #000; 
}
.footer-part > div > p{
    margin: 0;
    padding: 2px 8px;
}

.center-div {
    text-align: center;
}
