css:
body {
    font-family: Arial;
    line-height: normal;
    font-weight: bold;
}

/*.print-format {*/
/*  font-family: "Times New Roman", Times, serif;*/
/*}*/

p {
    color: black;
    font-size: 11px;
}

.border {
    border: 1px solid black;
    margin: 0;
    padding: 0 0 15px 0;
}

.header {
    border-bottom: 1px solid black;
}

.header > p {
    padding-left: 12px;
    font-size: 18px;
    font-weight: bold;
    /*margin : 0;*/
}

.comapany-address {
    height: auto;
    align-items: center;
}

.sub-header {
    font-weight: bold;
}

.company_name {
    font-size: 13px;
}

.flex-div, .two-seperate-box {
    display: flex;
    /*border-bottom: 1px solid black;*/
}

.flex-div > div {
    flex: 1;
}

.two-seperate-box > .three-column {
    width: 60%;
}

.two-seperate-box > .quote-details-div {
    width: 40%;
}

/*.flex-div > .three-column {*/
/*    width: 10;*/
/*}*/

.logo-container {
    display: flex;
    justify-content: center;
    height: auto;
    align-items: center;
    width: 32%;
}

table {
    border-collapse: collapse;
    width: 100%;
}

th, td {
    padding: 0;
    margin: 0;
    border: 1px solid black; 
    text-align: left;
}

.three-column {
    display: flex;
    flex-direction: column;
}

.company-details {
    display: flex;
    justify-content: space-between;
    /*gap: 10px;*/
    height: auto;
    align-items: center;
}

.protocol-address {
    padding-right: 25px;
}

.quote-details {
    display: flex;
    justify-content: space-between;
    border-bottom: 1px solid black;
    /*padding: 0 40px;*/
}

.quote-details-div {
    border-left: 1px solid black;
}

.quote-details > p {
    padding: 0px 10px;
}

.total {
    width: 15%;
}

.bill-ship-details {
    display: flex;
}

.bill-ship-details > div {
    flex: 1;
}

.bill-ship-address {
    display: flex;
    flex-direction: column;
}

.bill-ship-address > div > p, .bank-details{
    padding: 2px 12px;
    margin: 0;
}

.bank-details{
    padding: 2px 0px 0px 10px;
}

.bill-address {
    border-top: 1px solid black;
    border-bottom: 1px solid black;
}

.equal-div {
    display: flex;
}

.equal-div > div {
    flex: 1;
    /*border: 1px solid #000; */
    border-top: 1px solid #000; 
}
.equal-div > div > p{
    margin: 0;
    padding: 2px 8px;
}

.left-align {
    text-align: left;
}

.bordered-cell {
    border-left: 0px rgb(255, 255, 255);
    border-bottom: 0px rgb(255, 255, 255);
    border-top: 0px rgb(255, 255, 255);
}

.for-protocol {
    display: flex;
    justify-content: flex-end;
    padding: 10px 20px 0px 10px;
}

.digital_sign {
    display: flex;
    justify-content: flex-end;
    padding: 0px 20px 0px 10px;
}

.auth-sign {
    display: flex;
    justify-content: flex-end;
    padding: 0px 20px 0px 10px;
}


.footer {
    display: flex;
    justify-content: center;
}

.table-container{
    border-collapse: collapse;
    width: 100%;
}

.table-container td {
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    font-size: 12px !important;
    /*text-align: center;*/
}

.table-container th {
    background-color: #f2f2f2 !important;
    background-color: green;
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    color: black;
    font-weight: bold;
    text-align: center;
    font-size: 13px !important;
}

td.bordered-cell {
    border: none;
}

.tax-table {
    /*padding-top: 5px;*/
}

.cost-details {
    /*padding-top: 5px;*/
}

.center-div {
    text-align: center;
}

.digital_sign_div {
    padding-top: 5px;
    display: flex;
    flex-direction: column;
}

.auth-rep, .digital_sign{
    display: flex;
    justify-content: flex-end;
}

.auth-rep {
    padding-right: 5px;
}

.tax-table-container {
    display: flex;
    justify-content: center;
    padding-top: 10px;
    /*background-color: lightblue;*/
    /*width: 100%;*/
}

.tax-table-container > div {
    flex: 1;
    /*gap : 25px;*/
}

.custom-tax-breakup-table {
    /*background-color: green;*/
}

.custom-tax-breakup-table table, .footer-table table {
    width: 100% !important;
    border-collapse: collapse !important;
    margin: 0 !important;
    padding: 0 !important;
}

.custom-tax-breakup-table th, .custom-tax-breakup-table td, .footer-table th, .footer-table td {
    padding: 3px !important;
    margin: 15px !important;
    border: 1px solid #000 !important; 
    text-align: left !important;
    font-size: 12px !important;
}

.footer-table th, .footer-table td {
    border: none !important; 
}

.custom-tax-breakup-table th, .footer-table th {
    background-color: #f2f2f2 !important;
    color: black !important;
    color: black !important;
    font-weight: bold !important;
    font-size: 13px !important;
}

.footer-table th {
    background-color: white !important;
}
