css:
body {
    font-family: sans-serif;
    margin: 0 1px;
    padding: 0;
    line-height: normal;
    font-weight: bold;
}

.parent-div {
    border: 1px solid black;
    /*padding: 0 10px;*/
}

.space-from-border {
    padding: 0 10px;
}

.purchase-order {
    display: flex;
    justify-content: space-between;
}

.header {
    text-transform: uppercase;
    color: rgb(108, 107, 107);
    font-weight: bold;
    font-size: 25px;
}

h3 {
    font-size: 15px;
    font-weight: bold;
}

div, p {
    /*color: #252525;*/
    font-size: 11px;
    color: black;
}

.sub-header {
    font-weight: bold;
}

.logo {
    transform: translateY(20%);
}

.address {
    /*color: rgb(74, 74, 74);*/
}

b {
    color: black;
}

.bill-ship-to {
    display: flex;
    justify-content: space-between;
    padding-top: 2px;
}

.bill-ship-to > div {
    border: 1px solid black;
    /*flex: 1;*/
}

.po-details {
    /*transform: translateX(5%)*/
}

.heading {
    text-transform: uppercase;
    color: black;
    font-weight: bold;
}

.supply-source {
    display: flex;
    justify-content: flex-start;
    padding-top: 2px;
    /*gap: 20px; */
}

.supply-source div {
    flex: 1;
}

.destination {
    transform: translateX(-2%);
}

.cost-details {
    /*padding-top: 10px;*/
}

.address-container {
    padding-top: 5px;
}

.no-border-bottom {
    border-bottom: none !important;
}

.no-border-right {
    border-right: none !important;
}

.no-border-left {
    border-left: none !important;
}

.table-container, .address-table{
    border-collapse: collapse;
    width: 100%;
}

.table-container td, .address-table td {
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    font-size: 11px !important;
    /*text-align: center;*/
}

.table-container th, .address-table th {
    background-color: #f2f2f2 !important;
    background-color: green;
    border: 1px solid black;
    padding: 4px !important;
    margin: 0 !important;
    color: black;
    font-weight: bold;
    text-align: center;
}

td.bordered-cell {
    border: none;
}

.total {
    /* font-weight: bold; */
    width: 15%;
}
.total_amount {
    text-align: left;
    width: 25%;
    /*white-space: nowrap;*/
    overflow: hidden;
    text-overflow: ellipsis;
}

.bordered-cell {
    border-left: 0px rgb(255, 255, 255);
    border-bottom: 0px rgb(255, 255, 255);
    border-top: 0px rgb(255, 255, 255);
}

.bg-color {
    background-color: #f2f2f2;
}
.left-align {
    text-align: left;
}
.tax-table {
    padding-top: 20px;
}
.vertical-center {
    /*background-color: green;*/
    align-items: center;
}

.digital_sign_div {
    padding-top: 5px;
    display: flex;
    flex-direction: column;
}

.auth-rep, .digital_sign{
    display: flex;
    justify-content: flex-end;
}

.notes {
    display: flex;
    justify-content: space-between;
}
.footer {
    display: flex;
    justify-content: center;
}

.tax-table-container {
    display: flex;
    justify-content: center;
    padding-top: 10px;
    /*background-color: lightblue;*/
    /*width: 100%;*/
}

.tax-table-container > div {
    flex: 1;
    /*gap : 25px;*/
}

.custom-tax-breakup-table {
    /*background-color: green;*/
}

.custom-tax-breakup-table table {
    width: 100% !important;
    border-collapse: collapse !important;
    margin: 0 !important;
    padding: 0 !important;
}

.custom-tax-breakup-table th, 
.custom-tax-breakup-table td {
    padding: 3px !important;
    margin: 15px !important;
    border: 1px solid #000 !important; 
    text-align: left !important;
}

.custom-tax-breakup-table th {
    background-color: #f2f2f2 !important;
    color: black !important;
    font-weight: bold !important;
    padding: 4px !important;
}

td > .address {
    padding-left: 10px;
}

.top-space {
    padding-top: 10px;
}
.footer-table {
    width: 100% !important;
    /*border-collapse: collapse !important;*/
    margin: 0 !important;
    padding: 0 !important;

}

.company_name {
    font-size: 13px;   
}
