css:
body {
    font-family: Arial;
    margin: 0 1px;
    line-height: normal;
    font-weight: bold;
}

p {
    color: black;
    font-size: 11px;
}

.border {
    border: 1.5px solid black;
    margin: 0;
    padding: 0;
}

.header {
    border-bottom: 1px solid black;
    border-top: 1px solid black;
}

.header > p {
    padding-left: 12px;
    font-size: 23px;
    font-weight: bold;
    text-align: center;
}

.comapany-address {
    height: auto;
    align-items: center;
}

.sub-header {
    font-weight: bold;
}

.flex-div {
    display: flex;
    border-bottom: 1px solid black;
}

.flex-div > div {
    flex: 1;
}

.company_name {
    font-size: 14px;
}

.top-sapce {
    padding-top: 50px !important;
}

.logo-container {
    display: flex;
    justify-content: center;
    height: auto;
    align-items: center;
    /*width: 32%;*/
    /*padding-left: 15px;*/
    background-color: red;
}

table {
    border-collapse: collapse;
    width: 100%;
}

th, td {
    padding: 0;
    margin: 0;
    border: 1px solid black; 
    text-align: left;
}

.company-details {
    display: flex;
    justify-content: space-between;
    /*gap: 15px;*/
    height: auto;
    align-items: center;
    padding: 0 15px;

}

.total {
    width: 25%;
}

.bill-ship-details {
    display: flex;
    border-bottom: 1px solid black;
}

.bill-ship-details > div {
    flex: 1;
}

.bill-ship-address {
    display: flex;
    justify-content: space-between;
    padding: 0 5px;
}

.bill-ship-address > div > p {
    padding: 2px 12px;
    margin: 0;
}

.bank-details {
    margin: 0;
    padding: 0;
    padding-left: 20%;
}
.equal-div {
    display: flex;
}

.equal-div > div {
    flex: 1;
    /*border: 1px solid #000; */
    border-top: 2px solid #000; 
}
.equal-div > div > p{
    margin: 0;
    padding: 2px 8px;
}

.left-align {
    text-align: left;
}

.bordered-cell {
    border-left: 0px rgb(255, 255, 255);
    border-bottom: 0px rgb(255, 255, 255);
    border-top: 0px rgb(255, 255, 255);
}

.for-protocol {
    display: flex;
    justify-content: flex-end;
    padding: 10px 20px 0px 10px;
}

.digital_sign {
    display: flex;
    justify-content: flex-end;
    padding: 0px 20px 0px 10px;
}

.auth-sign {
    display: flex;
    justify-content: flex-end;
    padding: 0px 20px 0px 10px;
}


.footer {
    display: flex;
    justify-content: center;
}

.table-container{
    border-collapse: collapse;
    width: 100%;
}

.table-container td {
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    font-size: 12px!important;
    /*text-align: center;*/
}

.table-container th {
    background-color: #f2f2f2 !important;
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    font-size: 12px !important;
    color: black;
    font-weight: bold;
    text-align: center;
}

td.bordered-cell {
    border: none;
}

td > div {
    padding-left: 10px;
    /*background-color: blue;*/
}

.tax-table {
    padding-top: 10px;
}

.contact-details {
    display: flex;
    padding-top: 5px;
}

.cost-details {
    padding-top: 10px;
}

.center-div {
    text-align: center;
}

.total-amt {
    padding-right: 8px;
}

.digital_sign_div {
    padding-top: 5px;
    display: flex;
    flex-direction: column;
}

.digital_sign{
    display: flex;
    justify-content: flex-end;
}

.tcs-total {
    border-left: 0px solid black !important; 
    text-align: right;
    font-weight: bold !important;
}

.terms-conditions {
    /*padding-left: 10px;*/
    font-weight: bold;
}

.tc-details {
    /*padding-left: 10px;*/
}

.border-right-none {
    border-right: none !important;
}

.no-border {
    border-left: none !important;
    border-right: none !important;
    /*background-color: red;*/
}
