css:
@import url(https://fonts.googleapis.com/css?family=Open+Sans);


body {
    margin: 0 1px;
    line-height: normal;
    font-weight: bold;
}

.parent-div {
    border: 1px solid black;
    font-size: 11px;
    /*padding: 0 15px 2px 15px;*/
}

.print-format {
  /*font-family: 'Courier New', monospace!important;*/
  /*font-family: "Times New Roman", Times, serif;*/
  /*font-family: "Georgia", serif;*/
  /*font-family: "Merriweather", serif; */    
  /*font-family: 'Open Sans', serif;*/
  /*font-family: 'Garamond', serif;*/
}

p {
    color: black;
}

.delivery-challan {
    display: flex;
    justify-content: space-between;
    /*background-color: red;*/
    padding-bottom: 0 !important;
}

.header {
    text-transform: uppercase;
    color: rgb(108, 107, 107);
    font-weight: bold;
    font-size: 22px;
}

h3 {
    font-size: 15px;
    font-weight: bold;
}

.sub-header {
    font-weight: bold;
}

.logo {
    /*transform: translateY(-10%);*/
    /*background-color: red;*/
    padding: 10px 5px 0 0;
}

.address {
    /*font-size: 11px;*/
    transform: translateY(-17%); 
    color: rgb(74, 74, 74);
    border-bottom: 1px solid black;
}

.company-address {
    padding-bottom: 5px;
}

.space-from-border {
    /*border-bottom: 1px solid black;*/
    padding: 0px 6px 2px 10px ;
}

b {
    color: black;
    /*font-size: 12px;*/
}

.ship-details {
    display: flex;
    justify-content: space-between;
    /*background-color: red;*/
    border-bottom: 1px solid black
}

.ship-details > div {
    flex: 1;
    /*border: 1px solid black;*/
}

.dc-details {
    padding-left: 20px;
}

.heading {
    text-transform: uppercase;
    color: black;
    /*font-size: 12px;*/
    font-weight: bold;
}

.supply-source {
    display: flex;
    justify-content: flex-start;
}

.supply-source > div {
    flex: 1;
}

.cost-details {
    padding-top: 25px;
}

td.bordered-cell {
    border: none;
}


.total {
    /* font-weight: bold; */
    width: 15%;
}

.bordered-cell {
    border-left: 0px rgb(255, 255, 255);
    border-bottom: 0px rgb(255, 255, 255);
    border-top: 0px rgb(255, 255, 255);
}

.bg-color {
    background-color: #f2f2f2;
}

.footer {
    display: flex;
    justify-content: center;
}

.for-signature {
    display: flex;
    justify-content: space-between;
    padding-top: 10px;
}

.for-signature > div {
    /*flex: 1;*/
    width: auto;
    /*border: 1px solid black;*/
}

.display-column {
    display: flex;
    flex-direction: column;
    gap: 5px;
}


.table-container{
    border-collapse: collapse;
    width: 100%;
}

.table-container td {
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    /*font-size: 12px;*/
    /*text-align: center;*/
}

.table-container th {
    background-color: #f2f2f2 !important;
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    color: black;
    font-weight: bold;
    text-align: center;
    /*font-size: 12px;*/
}

td.bordered-cell {
    border: none;
}

.table-details {
    padding-top: 5px;
}

.company_name {
    font-size: 13px;   
}

.digital_sign_div {
	display: flex;
	flex-direction: column;
	width: 25%;
}

.auth-rep, .digital_sign{
	display: flex;
	justify-content: flex-end;
}

.digital_sign{
    padding: 15px 0;
}


.footer-table {
    width: 100% !important;
    /*border-collapse: collapse !important;*/
    margin: 0 !important;
    padding: 0 !important;
}


.service-footer{
    border-collapse: collapse;
    width: 100%;
}

.service-footer td {
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    text-align: center;
}

.service-footer th {
    background-color: #fff !important;
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    color: black;
    font-weight: bold;
    text-align: center;
}

.service-footer tr {
    padding: 6px !important;

}
