css:
body {
    font-family: Arial;
    margin: 0 1px;
    line-height: normal;
    font-weight: bold;
}

.company_name {
    font-size: 12px;
}

.stock-table, .address-table, .product-table, .footer-table {
    border-collapse: collapse;
    width: 100%;
}

.stock-table td, .address-table td, .product-table td, .footer-table td {
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    font-size: 11px !important;
}

.footer-table th{
    font-size: 12px !important;
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    color: black;
    font-weight: bold;
    text-align: center;
}

.stock-table th, .address-table th, .product-table th{
    /*background-color: #f2f2f2 !important;*/
    border: 1px solid black;
    padding: 3px !important;
    margin: 0 !important;
    font-size: 13px !important;
    color: black;
    font-weight: bold;
    text-align: center;
}

.product-table th {
    background-color: #f2f2f2 !important;
}


.address-table th, .product-table th {
    font-size: 12px !important;
    text-align: left;
}



.address-container, .table-container {
    width: 100%;
    /*display: flex;*/
    padding-top: 10px;
}

.footer-container {
    padding-top: 10px;
    width: 100%;
}

.footer-part {
    display: flex;
    flex-direction: column;
    gap: 10px;
    border: 1px solid black;
    padding: 3px 5px;
    margin: 0 !important;
}

.header {
    font-size: 18px !important;
}

.sub-header {
    font-weight: bold;
}

.center-div {
    text-align: center !important;
}
