css:
body {
    font-family: Arial;
}

p {
    font-size: 10px;
    color: #252525;
    letter-spacing: 0.5px;
    line-height: 1.5;
}

.border {
    border: 2px solid black;
    margin: 0;
    padding: 0 0 15px 0;
}

.header {
    border-bottom: 2px solid black;
}

.header > p{
    padding-left: 12px;
    font-size: 20px;
    font-weight: bold;
    /*margin : 0;*/
}

.comapany-address {
    height: auto;
    align-items: center;
}

.sub-header {
    font-weight: bold;
    font-size: 11px;
}

.total-tax {
    font-weight: bold;
    font-size: 14px;
    /*display: flex;*/
    /*justify-content: center;*/
}

.flex-div {
    display: flex;
}

.flex-div > div {
    flex: 1;
}

.logo-container {
    display: flex;
    justify-content: center;
    height: auto;
    align-items: center;
    width: 32%;
    padding-left: 15px;
    /*background-color: red;*/
}

table {
    border-collapse: collapse;
    width: 100%;
}

th, td {
    padding: 0;
    margin: 0;
    border: 2px solid black; 
    text-align: left;
}

th > h2 {
    padding: 0;
    margin: 0;
    text-align: left;
    font-size: 14px;
    color: Black;
    font-weight: Bold;
}

.three-column {
    display: flex;
    flex-direction: column;
}

.company-details {
    display: flex;
    justify-content: space-around;
    gap: 15px;
    height: auto;
    align-items: center;
}

.quote-details {
    display: flex;
    justify-content: space-between;
    border-bottom: 2px solid black;
}

.quote-details-div {
    border-left: 2px solid black;
}

.quote-details > p {
    padding: 0px 10px;
}

.total {
    width: 25%;
}

.bill-ship-details {
    display: flex;
}

.bill-ship-details > div {
    flex: 1;
}

.bill-ship-address {
    display: flex;
    flex-direction: column;
}

.bill-ship-address > div > p, .bank-details{
    padding: 2px 12px;
    margin: 0;
}

.bill-address {
    border-top: 2px solid black;
    border-bottom: 2px solid black;
}

.equal-div {
    display: flex;
}

.equal-div > div {
    flex: 1;
    /*border: 1px solid #000; */
    border-top: 2px solid #000; 
}
.equal-div > div > p{
    margin: 0;
    padding: 2px 8px;
}

.left-align {
    text-align: left;
}

.bordered-cell {
    border-left: 0px rgb(255, 255, 255);
    border-bottom: 0px rgb(255, 255, 255);
    border-top: 0px rgb(255, 255, 255);
}

.for-protocol {
    display: flex;
    justify-content: flex-end;
    padding: 10px 20px 0px 10px;
}

.digital_sign {
    display: flex;
    justify-content: flex-end;
    padding: 0px 20px 0px 10px;
}

.auth-sign {
    display: flex;
    justify-content: flex-end;
    padding: 0px 20px 0px 10px;
}


.footer {
    display: flex;
    justify-content: center;
}

.table-container{
    border-collapse: collapse;
    width: 100%;
}

.table-container td {
    border: 2px solid black;
    padding: 3px !important;
    margin: 0 !important;
    font-size: 10px;
    /*text-align: center;*/
}

.table-container th {
    background-color: #f2f2f2 !important;
    background-color: green;
    border: 2px solid black;
    padding: 3px !important;
    margin: 0 !important;
    color: black;
    font-weight: bold;
    text-align: center;
}

td.bordered-cell {
    border: none;
}

.tax-table {
    padding-top: 15px;
}

.cost-details {
    padding-top: 15px;
}

.center-div {
    text-align: center;
}
